css:
.print-format {
    font-size: 12px;
}
.main {
    width: 100%;
}

.top-header {
    display: flex;
    width: 100%;
    height: 1cm;
    justify-content: space-between;
    align-items: end;
}
.body {
    min-height: 25.7cm;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

.body > div {
    flex: 0 0 auto;
}


.item-details {
    flex-grow: 1 !important;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

.items-table-div .item-head-row {
    border-top: 1px solid black !important;
    border-bottom: 1px solid black !important;
}
.item-head-row {
    text-align: center;
     font-weight: bold;

}
.item-left-align-row {
    text-align: left !important;
}
.items-table-div > div {
    padding: 2px;
    text-align: right;
    word-break: break-all;
}
.items-table-div > div {
    border-left: 1px solid black;
}
.items-table-div .first-child{
    border-left: 0px;
}

.top-header > div {
    width: 33.33%;
}

.top-header > div:nth-child(2) {
    font-size: 125%;
    text-align: center;
}

.top-header > div:last-child {
    text-align: right;
}

.body {
    width: 100%;
    border: 1px solid black;
}

.company-details {
    width: 100%;
    padding: 3px;
    display: flex;
    justify-content: space-between;
}

.company-details > div {
    width: 33.33%;
}

.company-details > div:first-child {
    text-align: left;
}

.company-details > div:nth-child(2) {
    text-align: center;
}

.company-details > div:last-child {
    text-align: right;
}

.company-name {
    font-weight: bold;
    padding-top:5px;
    font-size: 14px;
}

.company-address {
    text-align: center;
    border-bottom: 1px solid black;
}

.customer-details {
    display: flex;
}

.customer-details > div {
    padding: 5px;
}

.customer-details > div:first-child {
    width: 50%;
    border-right: 1px solid black;
}

.customer-details > div:last-child {
    padding: 0px;
    width: 50%;
}

.customer-details > div >div:last-child {
    padding-left: 20px;
}

.invoice-no-and-date > div:first-child {
    padding: 5px;

}

.item-details, .tax-and-amount {
    border-bottom: 1px solid black;
}

.items-table {
    width: 100%;
}

.print-format td, .print-format th {
    padding: 3px !important;
}

.print-format th, .print-format td {
    color: black;
    font-size: 13px;
}

.items-table th {
    text-align: center;
}

.items-table td {
    text-align: right;
}

.items-table .left-align {
    text-align: left;
}

.items-table th, .items-table td{
    border: 1px solid black;
}

.items-table td{
    border-top: 0px;
    border-bottom: 0px;
}

.items-table th:first-child, .items-table td:first-child{

    border-left: 0px;
}

.items-table th:last-child, .items-table td:last-child{
    border-right: 0px;
}

.item-empty-row {
    grid-row: span 1000;
}

.items-table-div {
    width: 100%;
    display: grid;
    flex-grow: 1;
    min-height: 0;
}

.item-table-div {
    min-height: 0;
}

.item-cgst {
    border-right: 0px !important;
}

.item-sgst {
    border-left: 0px !important;
}

.tax-and-amount {
    display: flex;
    justify-content: space-between;
}

.tax-and-amount > .tax-and-amount-left {
    width: 65%;
    border-right: 1px solid black;
}

.tax-and-amount > .tax-and-amount-right {
    width: 35%;
}

.tax-and-amount-left {
    display: flex;
    flex-direction: column;
}
.tax-table table {
    width: 100%;
}

.tax-table td {
    border: 1px solid black;
    border-top: 0px;
}

.tax-table td:first-child {
    border-top: 0px;
    border-left: 0px;
}

.tax-table td:last-child {
    border-right: 0px;
}

.bank-acc-details table {
    width: 100%;
}

.bank-acc-details table td:last-child {
    width: 80%;
}

.bank-acc-details td {
    font-weight: bold;
}

.amount-in-words {
    width: 100%;
    border-top: 1px solid black;
    padding: 5px;
}

.tax-and-amount-right > div {
    display: flex;
    justify-content: space-between;
}

.tax-and-amount-right > div > div {
    padding-left: 5px;
    padding-bottom: 4px;
    padding-right: 5px;
}

.tax-and-amount-right > div:last-child {
    font-weight: bold;
    border-top: 1px solid black;
    border-bottom: 1px solid black;
}

.footer {
    display: flex;
    justify-content: space-between;
}

.footer > div {
    height: 2cm;
    display: flex;
    align-items: end;
    justify-content: space-between;
    width: 33.33%;
    padding: 5px;
}

.footer > div:first-child {
    text-align: left;
}

.footer > div:nth-child(2) {
    text-align: center;
    border-left: 1px solid black;
    border-right: 1px solid black;
}

.footer > div > div {
    width: 100%;
}

.footer > div:first-child {
    flex-direction: column;
}

.footer > div:last-child {
    flex-direction: column;
    text-align: right;
}

.footer > div:last-child > div:first-child {
    font-weight: bold;
}
